import React, { Suspense, useRef, useState, useEffect } from 'react';
import { Canvas, useLoader, useThree } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';
import * as THREE from 'three';

function BoxWall({ position, rotation, scale, artwork }) {
  const texture = useLoader(THREE.TextureLoader, artwork);
  if (texture) {
    texture.encoding = 3000;  // Directly setting encoding to the constant value for sRGB
  }

  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <planeGeometry args={[scale[0], scale[1]]} />
      <meshStandardMaterial map={texture} side={THREE.DoubleSide} />
    </mesh>
  );
}

function Room() {
  const wallMaterial = new THREE.MeshStandardMaterial({
    color: 0xF8F8FF, // Off-white color
    roughness: 0.5, // Adjust as needed
    metalness: 0.1 // Adjust as needed
  });

  const floorMaterial = new THREE.MeshStandardMaterial({
    color: 0xF8F8FF, // Off-white color
    roughness: 0.7, // Adjust as needed
    metalness: 0.1 // Adjust as needed
  });

  return (
    <>
      {/* Floor and Ceiling */}
      <Plane args={[10, 10]} rotation={[-Math.PI / 2, 0, 0]} position={[0, -2.5, 0]} material={floorMaterial} />
      <Plane args={[10, 10]} rotation={[Math.PI / 2, 0, 0]} position={[0, 2.5, 0]} material={floorMaterial} />
      {/* Walls */}
      <Plane args={[10, 5]} position={[-5, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[10, 5]} position={[5, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[10, 5]} position={[0, 0, 5]} rotation={[0, Math.PI, 0]} material={wallMaterial} />
      <Plane args={[10, 5]} position={[0, 0, -5]} material={wallMaterial} />
    </>
  );
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} />
        <pointLight position={[10, 10, 10]} />
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
