import { useRef, useEffect } from 'react';
import { useThree, useFrame } from '@react-three/fiber';

function CameraControls() {
  const { camera } = useThree();
  const moveForward = useRef(false);
  const moveBackward = useRef(false);

  // Define front and back boundaries of the room
  const zFrontBoundary = 10; // Front wall position
  const zBackBoundary = -10; // Back wall position

  useFrame(() => {
    if (moveForward.current && camera.position.z > zBackBoundary) {
      camera.position.z -= 0.1; // Adjust to move forward towards the back wall
    }
    if (moveBackward.current && camera.position.z < zFrontBoundary) {
      camera.position.z += 0.1; // Adjust to move backward towards the front wall
    }
  });

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowUp') {
        moveForward.current = true;
      } else if (event.key === 'ArrowDown') {
        moveBackward.current = true;
      }
    };

    const handleKeyUp = (event) => {
      if (event.key === 'ArrowUp') {
        moveForward.current = false;
      } else if (event.key === 'ArrowDown') {
        moveBackward.current = false;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return null; // This component does not render anything
}

export default CameraControls;
