// App.js
import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { HemisphereLight, DirectionalLight, AmbientLight } from 'three';
import Room from './Room';

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }} colorManagement>
        <ambientLight intensity={0.4} color="#ffffff" />
        <HemisphereLight args={['#ffffbb', '#080820', 1.5]} position={[0, 50, 0]} />
        <DirectionalLight args={['#ffffff', 0.5]} position={[0, 10, 0]} rotation={[-Math.PI / 2, 0, 0]} />
        <Room />
        <OrbitControls enableZoom={false} enablePan={true} enableRotate={true} />
      </Canvas>
    </div>
  );
}

export default App;
