import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { HemisphereLight, DirectionalLight } from 'three'; // Import from 'three'
import Room from './Room';

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas>
        <ambientLight intensity={0.3} color="#ffffff" />
        <HemisphereLight skyColor={"#ffffff"} groundColor={"#000000"} intensity={0.5} position={[0, 50, 0]} />
        <DirectionalLight color={"#ffffff"} intensity={1.5} position={[0, 20, 0]} /> {/* Simulates sunlight */}
        <Room />
        <Environment preset="sunset" background={false} />
        <OrbitControls enableZoom={false} enablePan={true} enableRotate={true} />
      </Canvas>
    </div>
  );
}

export default App;
