import React from 'react';
import { useThree } from '@react-three/fiber';
import { Plane } from '@react-three/drei';
import * as THREE from 'three'; // Import THREE

function Room() {
  const { scene } = useThree();
  scene.background = new THREE.Color(0xffffff); // Set a white background

  return (
    <>
      {/* Planes with basic white material */}
      <Plane args={[20, 10]} position={[-10, 1, 0]} rotation={[0, Math.PI / 2, 0]} material={{ color: 'white' }} />
      <Plane args={[20, 10]} position={[10, 1, 0]} rotation={[0, -Math.PI / 2, 0]} material={{ color: 'white' }} />
      <Plane args={[20, 10]} position={[0, 1, 10]} rotation={[0, Math.PI, 0]} material={{ color: 'white' }} />
      <Plane args={[20, 10]} position={[0, 1, -10]} material={{ color: 'white' }} />
      <Plane args={[20, 10]} position={[0, 11, 0]} rotation={[-Math.PI / 2, 0, 0]} material={{ color: 'white' }} /> {/* Ceiling */}
      <Plane args={[20, 10]} position={[0, -9, 0]} rotation={[Math.PI / 2, 0, 0]} material={{ color: 'white' }} /> {/* Floor */}
    </>
  );
}

export default Room;
