import React, { useRef, useEffect } from 'react';
import { Canvas, useThree, useFrame } from '@react-three/fiber';
import { OrbitControls, Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  const lightGreyMaterial = new THREE.MeshStandardMaterial({ color: '#f0f0f0' });

  return (
    <>
      <Plane args={[20, 8]} position={[-10, 1, 0]} rotation={[0, Math.PI / 2, 0]} material={lightGreyMaterial} />
      <Plane args={[20, 8]} position={[10, 1, 0]} rotation={[0, -Math.PI / 2, 0]} material={lightGreyMaterial} />
      <Plane args={[20, 8]} position={[0, 1, 10]} rotation={[0, Math.PI, 0]} material={lightGreyMaterial} />
      <Plane args={[20, 8]} position={[0, 1, -10]} material={lightGreyMaterial} />
      <Plane args={[30, 30]} position={[0, -3, 0]} rotation={[-Math.PI / 2, 0, 0]} material={lightGreyMaterial} />
    </>
  );
}

function CameraControls() {
  const { camera, gl } = useThree();
  const moveForward = useRef(false);
  const moveBackward = useRef(false);
  const turnLeft = useRef(false);
  const turnRight = useRef(false);

  useFrame(() => {
    const speed = 0.05;
    if (moveForward.current) {
      camera.position.z -= speed;
    }
    if (moveBackward.current) {
      camera.position.z += speed;
    }
    if (turnLeft.current) {
      camera.rotation.y += 0.01;
    }
    if (turnRight.current) {
      camera.rotation.y -= 0.01;
    }
  });

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = true;
          break;
        case 'ArrowDown':
          moveBackward.current = true;
          break;
        case 'ArrowLeft':
          turnLeft.current = true;
          break;
        case 'ArrowRight':
          turnRight.current = true;
          break;
        default:
          break;
      }
    };

    const handleKeyUp = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = false;
          break;
        case 'ArrowDown':
          moveBackward.current = false;
          break;
        case 'ArrowLeft':
          turnLeft.current = false;
          break;
        case 'ArrowRight':
          turnRight.current = false;
          break;
        default:
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return null;
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1.3} />
        <Room />
        <CameraControls />
        <OrbitControls enableZoom={false} enablePan={false} enableRotate={false} />
      </Canvas>
    </div>
  );
}

export default App;
