import React from 'react';
import { useLoader } from '@react-three/fiber';
import * as THREE from 'three';

function Wall({ position, rotation, scale, color = '#FFFFFF' }) {
  return (
    <mesh position={position} rotation={rotation}>
      <planeGeometry args={scale} /> // Corrected to planeGeometry
      <meshStandardMaterial color={color} /> // No need for attach="material"
    </mesh>
  );
}

function Artwork({ position, rotation, image }) {
  const texture = useLoader(THREE.TextureLoader, image);
  return (
    <mesh position={position} rotation={rotation}>
      <planeGeometry args={[3, 2]} /> // Corrected to planeGeometry
      <meshBasicMaterial map={texture} /> // No need for attach="material"
    </mesh>
  );
}

function Room() {
  return (
    <group>
      {/* Walls */}
      <Wall position={[0, 2, -5]} rotation={[0, 0, 0]} scale={[10, 4, 1]} />
      <Wall position={[0, 2, 5]} rotation={[0, Math.PI, 0]} scale={[10, 4, 1]} />
      <Wall position={[-5, 2, 0]} rotation={[0, Math.PI / 2, 0]} scale={[10, 4, 1]} />
      <Wall position={[5, 2, 0]} rotation={[0, -Math.PI / 2, 0]} scale={[10, 4, 1]} />
      <Wall position={[0, 6, 0]} rotation={[-Math.PI / 2, 0, 0]} scale={[10, 10, 1]} color="#E0E0E0" /> {/* Ceiling */}
      <Wall position={[0, -0.1, 0]} rotation={[Math.PI / 2, 0, 0]} scale={[10, 10, 1]} color="#E0E0E0" /> {/* Floor */}

      {/* Artworks */}
      <Artwork position={[-3, 2, -4.99]} rotation={[0, 0, 0]} image="/path-to-your-artwork1.jpg" />
      <Artwork position={[3, 2, -4.99]} rotation={[0, 0, 0]} image="/path-to-your-artwork2.jpg" />
    </group>
  );
}

export default Room;
