// App.js
import React, { useRef, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three'; // Import THREE from three.js
import Room from './Room';
import * as THREE from 'three'; // Import THREE

function App() {
  const orbitControls = useRef();
  const [moveSpeed, setMoveSpeed] = useState(0.1); // Adjust the move speed here

  const handleKeyDown = (event) => {
    switch (event.key) {
      case 'ArrowUp':
        orbitControls.current?.target.add(new THREE.Vector3(0, moveSpeed, 0));
        break;
      case 'ArrowDown':
        orbitControls.current?.target.add(new THREE.Vector3(0, -moveSpeed, 0));
        break;
      case 'ArrowLeft':
        orbitControls.current?.target.add(new THREE.Vector3(-moveSpeed, 0, 0));
        break;
      case 'ArrowRight':
        orbitControls.current?.target.add(new THREE.Vector3(moveSpeed, 0, 0));
        break;
      case 'w':
        orbitControls.current?.dolly(0.1); // Move camera forwards
        break;
      case 's':
        orbitControls.current?.dolly(-0.1); // Move camera backwards
        break;
      default:
        break;
    }
  };

  return (
    <div
      className="App"
      style={{ height: '100vh', width: '100vw' }}
      onKeyDown={handleKeyDown}
      tabIndex={0} // Ensure the div can receive focus
    >
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls ref={orbitControls} enablePan={false} />
      </Canvas>
    </div>
  );
}

export default App;
