import React, { Suspense, useRef } from 'react';
import { Canvas, useLoader } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';
import * as THREE from 'three';

function BoxWall({ position, rotation, scale, color }) {
  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={color} side={THREE.DoubleSide} />
    </mesh>
  );
}

function Room() {
  return (
    <>
      {/* Walls */}
      <BoxWall position={[-2.5, 1.5, 0]} rotation={[0, Math.PI / 2, 0]} scale={[0.1, 3, 5]} color="lightgrey" />
      <BoxWall position={[2.5, 1.5, 0]} rotation={[0, -Math.PI / 2, 0]} scale={[0.1, 3, 5]} color="lightgrey" />
      <BoxWall position={[0, 1.5, -2.5]} rotation={[0, 0, 0]} scale={[5, 3, 0.1]} color="lightgrey" />
      <BoxWall position={[0, 1.5, 2.5]} rotation={[0, Math.PI, 0]} scale={[5, 3, 0.1]} color="lightgrey" />
      {/* Floor and Ceiling */}
      <BoxWall position={[0, 0, 0]} rotation={[-Math.PI / 2, 0, 0]} scale={[5, 5, 0.1]} color="lightblue" />
      <BoxWall position={[0, 3, 0]} rotation={[Math.PI / 2, 0, 0]} scale={[5, 5, 0.1]} color="white" />
    </>
  );
}

function App() {
  return (
    <div style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 2, 5], fov: 75 }}>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
