function BoxWall({ position, rotation, scale, artwork }) {
  const texture = useLoader(THREE.TextureLoader, artwork);
  texture.wrapS = THREE.RepeatWrapping;
  texture.wrapT = THREE.RepeatWrapping;
  texture.repeat.set(scale[0] / 5, scale[1] / 5); // This will need adjustment based on your texture size

  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <planeGeometry args={[scale[0], scale[1]]} />
      <meshStandardMaterial map={texture} side={THREE.DoubleSide} />
    </mesh>
  );
}

function Room() {
  const [selectedArt, setSelectedArt] = useState(null);
  const { camera, gl: { domElement } } = useThree();
  const controls = useRef();

  useEffect(() => {
    const checkBounds = () => {
      camera.position.clamp(new THREE.Vector3(-3, 1, -3), new THREE.Vector3(3, 3, 3));
    };

    const currentControls = controls.current;
    currentControls.addEventListener('change', checkBounds);
    return () => currentControls.removeEventListener('change', checkBounds);
  }, [camera, controls]);

  return (
    <>
      <OrbitControls ref={controls} args={[camera, domElement]} enableZoom={false} enablePan={false} enableRotate={true} />
      <Suspense fallback={null}>
        {/* Scale up the dimensions for each wall, floor, and ceiling */}
        <BoxWall position={[-3.5, 0, 0]} rotation={[0, Math.PI / 2, 0]} scale={[7, 5, 0.1]} artwork="/assets/images/wall.jpg" />
        <BoxWall position={[3.5, 0, 0]} rotation={[0, -Math.PI / 2, 0]} scale={[7, 5, 0.1]} artwork="/assets/images/wall.jpg" />
        <BoxWall position={[0, 0, -3.5]} rotation={[0, 0, 0]} scale={[7, 5, 0.1]} artwork="/assets/images/wall.jpg" />
        <BoxWall position={[0, 0, 3.5]} rotation={[0, Math.PI, 0]} scale={[7, 5, 0.1]} artwork="/assets/images/wall.jpg" />
        <BoxWall position={[0, 2.5, 0]} rotation={[-Math.PI / 2, 0, 0]} scale={[7, 7, 0.1]} artwork="/assets/images/ceiling.jpg" />
        <BoxWall position={[0, -2.5, 0]} rotation={[Math.PI / 2, 0, 0]} scale={[7, 7, 0.1]} artwork="/assets/images/floor.jpg" />
      </Suspense>
      {selectedArt && (
        <Html position={[0, 0, 0]} center>
          <div className="art-details" style={{ background: 'white', padding: '10px' }}>
            {selectedArt}
          </div>
        </Html>
      )}
    </>
  );
}
