import React from 'react';
import { Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  const wallMaterial = new THREE.MeshStandardMaterial({
    color: '#ffffff', // Light grey for uniform wall appearance
    roughness: 0.5,   // Matte finish
    metalness: 0.1    // Minimal reflectivity
  });

  return (
    <>
      {/* Using the same material for each wall to ensure color consistency */}
      <Plane args={[10, 10]} position={[-5, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[10, 10]} position={[5, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[10, 10]} position={[0, 0, 5]} rotation={[0, Math.PI, 0]} material={wallMaterial} />
      <Plane args={[10, 10]} position={[0, 0, -5]} material={wallMaterial} />
      {/* Optionally remove the ceiling or use the same material */}
      {/* <Plane args={[10, 10]} position={[0, 5, 0]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} /> */}
      <Plane args={[10, 10]} position={[0, -5, 0]} rotation={[Math.PI / 2, 0, 0]} material={wallMaterial} />
    </>
  );
}

export default Room;
