import React, { useRef, useEffect } from 'react';
import { Canvas, useFrame, useThree, extend } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

// Extend will make OrbitControls available as a JSX element called orbitControls for use within the Canvas
extend({ OrbitControls });

function Controls() {
  const { camera, gl: { domElement } } = useThree();
  const controls = useRef();

  useFrame(() => {
    if (controls.current) {
      controls.current.update();
    }
  });

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          camera.position.z -= 0.1;
          break;
        case 'ArrowDown':
          camera.position.z += 0.1;
          break;
        case 'ArrowLeft':
          camera.rotation.y += 0.02;
          break;
        case 'ArrowRight':
          camera.rotation.y -= 0.02;
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [camera]);

  return (
    <orbitControls ref={controls} args={[camera, domElement]} enableZoom={false} enableRotate={true} enablePan={false} />
  );
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <Controls />
      </Canvas>
    </div>
  );
}

export default App;
