import React, { Suspense, useRef, useState, useEffect } from 'react';
import { Canvas, useLoader, useThree } from '@react-three/fiber';
import { OrbitControls, Html, Plane } from '@react-three/drei';
import * as THREE from 'three';

function BoxWall({ position, rotation, scale, artwork }) {
  const texture = useLoader(THREE.TextureLoader, artwork);
  texture.wrapS = THREE.RepeatWrapping;
  texture.wrapT = THREE.RepeatWrapping;
  // Adjust texture repeat based on the actual size of the texture and the scale of the plane
  texture.repeat.set(scale[0] / 5, scale[1] / 5);

  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <planeGeometry args={[scale[0], scale[1]]} />
      <meshStandardMaterial map={texture} side={THREE.DoubleSide} />
    </mesh>
  );
}

function Room() {
  const [selectedArt, setSelectedArt] = useState(null);

  return (
    <>
      <OrbitControls enableZoom={false} enablePan={false} enableRotate={true} />
      <Suspense fallback={null}>
        {/* Floor and ceiling larger */}
        <BoxWall position={[0, -2.5, 0]} rotation={[-Math.PI / 2, 0, 0]} scale={[30, 10, 0.1]} artwork="/assets/images/floor.jpg" />
        <BoxWall position={[0, 2.5, 0]} rotation={[Math.PI / 2, 0, 0]} scale={[30, 10, 0.1]} artwork="/assets/images/ceiling.jpg" />

        {/* Walls remain smaller */}
        <BoxWall position={[-15, 0, 0]} rotation={[0, Math.PI / 2, 0]} scale={[30, 5, 0.1]} artwork="/assets/images/wall.jpg" />
        <BoxWall position={[15, 0, 0]} rotation={[0, -Math.PI / 2, 0]} scale={[30, 5, 0.1]} artwork="/assets/images/wall.jpg" />
        <BoxWall position={[0, 0, -15]} rotation={[0, 0, 0]} scale={[30, 5, 0.1]} artwork="/assets/images/wall.jpg" />
        <BoxWall position={[0, 0, 15]} rotation={[0, Math.PI, 0]} scale={[30, 5, 0.1]} artwork="/assets/images/wall.jpg" />
      </Suspense>
      {selectedArt && (
        <Html position={[0, 0, 0]} center>
          <div className="art-details" style={{ background: 'white', padding: '10px' }}>
            {selectedArt}
          </div>
        </Html>
      )}
    </>
  );
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 5], fov: 75 }}>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
