import React from 'react';
import { useThree } from '@react-three/fiber';
import { Plane } from '@react-three/drei';
import * as THREE from 'three'; // Make sure this import is here

function Room() {
  const { scene } = useThree();
  scene.background = new THREE.Color(0xffffff); // Set a white background

  // Define the wall material
  const wallMaterial = new THREE.MeshStandardMaterial({
    color: '#f0f0f0', // Light grey
    roughness: 0.5,   // Adjust for matte appearance
    metalness: 0.1    // Very low reflectivity
  });

  return (
    <>
      {/* Apply the wallMaterial to each Plane */}
      <Plane args={[20, 10]} position={[-10, 1, 0]} rotation={[0, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[10, 1, 0]} rotation={[0, -Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[0, 1, 10]} rotation={[0, Math.PI, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[0, 1, -10]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[0, 11, 0]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} /> {/* Ceiling */}
      <Plane args={[20, 10]} position={[0, -9, 0]} rotation={[Math.PI / 2, 0, 0]} material={wallMaterial} /> {/* Floor */}
    </>
  );
}

export default Room;
