// App.js
import React, { useRef, useEffect, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';
import * as THREE from 'three';

function App() {
  const orbitControls = useRef();
  const canvasRef = useRef();
  const [moveSpeed, setMoveSpeed] = useState(0.1);

  useEffect(() => {
    canvasRef.current.focus();
  }, []);

  const handleKeyDown = (event) => {
    switch (event.key) {
      case 'ArrowUp':
        orbitControls.current?.rotateX(-0.01); // Rotate up
        break;
      case 'ArrowDown':
        orbitControls.current?.rotateX(0.01); // Rotate down
        break;
      case 'ArrowLeft':
        orbitControls.current?.rotateY(0.01); // Rotate left
        break;
      case 'ArrowRight':
        orbitControls.current?.rotateY(-0.01); // Rotate right
        break;
      case 'w':
        orbitControls.current?.dolly(0.1); // Move camera forwards
        break;
      case 's':
        orbitControls.current?.dolly(-0.1); // Move camera backwards
        break;
      default:
        break;
    }
  };

  return (
    <div
      className="App"
      style={{ height: '100vh', width: '100vw' }}
      onKeyDown={handleKeyDown}
      tabIndex={0}
      ref={canvasRef} // Add ref to the canvas
    >
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls ref={orbitControls} enablePan={false} />
      </Canvas>
    </div>
  );
}

export default App;
