import React from 'react';
import { useThree } from '@react-three/fiber';
import { Plane } from '@react-three/drei';
import * as THREE from 'three'; // Ensure THREE is properly imported

function Room() {
  // Material for all walls and the floor with specified color and less reflective properties
  const wallMaterial = new THREE.MeshStandardMaterial({
    color: '#f0f0f0', // Light grey color
    roughness: 0.5,   // Matte surface to reduce reflections
    metalness: 0.1    // Low metalness to avoid shininess
  });

  return (
    <>
      {/* Walls with the same material */}
      <Plane args={[20, 10]} position={[-10, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[10, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[0, 0, 10]} rotation={[0, Math.PI, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[0, 0, -10]} material={wallMaterial} />

      {/* Floor */}
      <Plane args={[20, 10]} position={[0, -10, 0]} rotation={[Math.PI / 2, 0, 0]} material={wallMaterial} />
    </>
  );
}

export default Room;
