import React, { useState, useEffect } from 'react';
import { Canvas, useThree } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { AmbientLight, PointLight, Vector3 } from 'three';
import Room from './Room';

function CameraController() {
  const { camera, gl } = useThree();

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          // Move forward
          camera.position.add(camera.getWorldDirection(new Vector3()).multiplyScalar(0.5));
          break;
        case 'ArrowDown':
          // Move backward
          camera.position.add(camera.getWorldDirection(new Vector3()).multiplyScalar(-0.5));
          break;
        case 'ArrowLeft':
          // Rotate left
          camera.rotation.y += 0.1;
          break;
        case 'ArrowRight':
          // Rotate right
          camera.rotation.y -= 0.1;
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [camera]);

  return null;
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1.3} color="#ffffff" />
        <CameraController />
        <Room />
        <OrbitControls enableZoom={false} enablePan={false} enableRotate={false} />
      </Canvas>
    </div>
  );
}

export default App;
