import React, { useRef, useState } from 'react';
import { Canvas, useLoader } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';
import * as THREE from 'three';

function BoxWall({ position, rotation, scale, artwork }) {
  const texture = useLoader(THREE.TextureLoader, artwork);
  const mesh = useRef();

  return (
    <mesh position={position} rotation={rotation} scale={scale} ref={mesh}>
      <planeGeometry args={[1, 1]} />
      <meshStandardMaterial map={texture} side={THREE.DoubleSide} />
    </mesh>
  );
}

function Room() {
  const [selectedArt, setSelectedArt] = useState(null);

  return (
    <Canvas camera={{ position: [0, 0, 5], fov: 75 }}>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      {/* Walls */}
      <BoxWall position={[-2.5, 0, 0]} rotation={[0, Math.PI / 2, 0]} scale={[5, 5, 0.1]} artwork="/textures/wall.jpg" />
      <BoxWall position={[2.5, 0, 0]} rotation={[0, -Math.PI / 2, 0]} scale={[5, 5, 0.1]} artwork="/textures/wall.jpg" />
      <BoxWall position={[0, 0, -2.5]} rotation={[0, 0, 0]} scale={[5, 5, 0.1]} artwork="/textures/wall.jpg" />
      <BoxWall position={[0, 0, 2.5]} rotation={[0, Math.PI, 0]} scale={[5, 5, 0.1]} artwork="/textures/wall.jpg" />
      {/* Ceiling and Floor */}
      <BoxWall position={[0, 2.5, 0]} rotation={[-Math.PI / 2, 0, 0]} scale={[5, 5, 0.1]} artwork="/textures/ceiling.jpg" />
      <BoxWall position={[0, -2.5, 0]} rotation={[Math.PI / 2, 0, 0]} scale={[5, 5, 0.1]} artwork="/textures/floor.jpg" />

      <OrbitControls />
      {selectedArt && (
        <Html position={[0, 0, 0]} center>
          <div className="art-details" style={{ background: 'white', padding: '10px' }}>
            {selectedArt}
          </div>
        </Html>
      )}
    </Canvas>
  );
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas style={{ background: 'black' }}>
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
