import React from 'react';
import { Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  const wallMaterial = new THREE.MeshStandardMaterial({
    color: '#f0f0f0', // Light grey for walls and ceiling
    roughness: 0.5,
    metalness: 0.1
  });

  // Define the ceiling thickness here; adjust as necessary, 0.1 as a placeholder
  const ceilingThickness = 10;

  // Dimensions for the ceiling and the skylight
  const ceilingSize = 20; // Total size of the ceiling
  const skylightSize = ceilingSize / 2; // Skylight is 50% of the ceiling size
  const ceilingBorderWidth = (ceilingSize - skylightSize) / 2; // Border width around the skylight

  return (
    <>
      {/* Walls */}
      <Plane args={[ceilingSize, 10]} position={[-10, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[ceilingSize, 10]} position={[10, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[ceilingSize, 10]} position={[0, 0, 10]} rotation={[0, Math.PI, 0]} material={wallMaterial} />
      <Plane args={[ceilingSize, 10]} position={[0, 0, -10]} material={wallMaterial} />

      {/* Floor */}
      <Plane args={[ceilingSize, ceilingSize]} position={[0, -10, 0]} rotation={[Math.PI / 2, 0, 0]} material={wallMaterial} />

      {/* Ceiling with a skylight */}
      <Plane args={[ceilingBorderWidth, ceilingSize, ceilingThickness]} position={[-(skylightSize/2 + ceilingBorderWidth/2), 10, 0]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} />
      <Plane args={[ceilingBorderWidth, ceilingSize, ceilingThickness]} position={[(skylightSize/2 + ceilingBorderWidth/2), 10, 0]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} />
      <Plane args={[skylightSize, ceilingBorderWidth, ceilingThickness]} position={[0, 10, -(skylightSize/2 + ceilingBorderWidth/2)]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} />
      <Plane args={[skylightSize, ceilingBorderWidth, ceilingThickness]} position={[0, 10, (skylightSize/2 + ceilingBorderWidth/2)]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} />
    </>
  );
}

export default Room;
