import React from 'react';
import { useThree } from '@react-three/fiber';
import { Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  const lightGreyMaterial = new THREE.MeshStandardMaterial({
    color: '#f0f0f0', // Uniform light grey color
    roughness: 0.4,   // Slightly reflective to enhance uniformity under different lighting
    metalness: 0.1    // Minimal metalness
  });

  return (
    <>
      {/* Walls */}
      <Plane args={[20, 10]} position={[-10, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={lightGreyMaterial} />
      <Plane args={[20, 10]} position={[10, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={lightGreyMaterial} />
      <Plane args={[20, 10]} position={[0, 0, 10]} rotation={[0, Math.PI, 0]} material={lightGreyMaterial} />
      <Plane args={[20, 10]} position={[0, 0, -10]} material={lightGreyMaterial} />

      {/* Floor */}
      <Plane args={[20, 10]} position={[0, -10, 0]} rotation={[Math.PI / 2, 0, 0]} material={lightGreyMaterial} />

      {/* Optionally add a ceiling or skylight if needed */}
      {/* <Plane args={[20, 10]} position={[0, 10, 0]} rotation={[-Math.PI / 2, 0, 0]} material={lightGreyMaterial} /> */}
    </>
  );
}

export default Room;
