import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Environment, PerspectiveCamera } from '@react-three/drei';
import Room from './Room'; // Ensure the path is correct

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas>
        <PerspectiveCamera makeDefault position={[0, 1, 20]} fov={75} />
        <ambientLight intensity={1.0} color="#ffffff" /> // Increased ambient light for overall brightness
        <pointLight position={[0, 50, 50]} intensity={1.5} color="#ffffff" /> // Stronger white light to brighten the scene
        <Room />
        <OrbitControls enableZoom={false} enablePan={true} enableRotate={true} />
      </Canvas>
    </div>
  );
}

export default App;
