import React, { useRef } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three'; // Import THREE to access Three.js exports

function BoxWall(props) {
  // This component creates a wall with a picture texture.
  const texture = useLoader(THREE.TextureLoader, props.artwork); // Proper usage of useLoader
  const mesh = useRef();

  useFrame(() => {
    // Animations or dynamic interactions can be added here.
  });

  return (
    <mesh
      {...props}
      ref={mesh}
      onClick={() => props.onClick()}>
      <boxGeometry args={[10, 5, 0.1]} />  // Correctly use boxGeometry from three
      <meshStandardMaterial map={texture} />  // Correctly use meshStandardMaterial from three
    </mesh>
  );
}

function Room() {
  const [selectedArt, setSelectedArt] = useState(null);

  return (
    <Canvas>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <BoxWall position={[-5, 0, 0]} artwork="/path-to-art1.jpg" onClick={() => setSelectedArt('Art 1 Description')} />
      <BoxWall position={[5, 0, 0]} artwork="/path-to-art2.jpg" onClick={() => setSelectedArt('Art 2 Description')} />
      <BoxWall position={[0, 0, -5]} artwork="/path-to-art3.jpg" onClick={() => setSelectedArt('Art 3 Description')} />
      <OrbitControls />
      {selectedArt && <div className="art-details">{selectedArt}</div>}
    </Canvas>
  );
}

function App() {
  return (
    <div className="App">
      <Room />
    </div>
  );
}

export default App;
