import React, { useRef, useState } from 'react';
import { Canvas, useLoader, useFrame } from '@react-three/fiber';
import { OrbitControls, MeshStandardMaterial, BoxGeometry } from '@react-three/drei';
import {TextureLoader} from 'three';



function BoxWall(props) {
  // This component creates a wall with a picture texture.
  const texture = useLoader(TextureLoader, props.artwork);
  const mesh = useRef();

  useFrame(() => {
    // This can include animations or dynamic interactions with the wall.
  });

  return (
    <mesh
      {...props}
      ref={mesh}
      onClick={() => props.onClick()}>
      <BoxGeometry args={[10, 5, 0.1]} />
      <MeshStandardMaterial map={texture} />
    </mesh>
  );
}

function Room() {
  const [selectedArt, setSelectedArt] = useState(null);

  return (
    <Canvas>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <BoxWall position={[-5, 0, 0]} artwork="/path-to-art1.jpg" onClick={() => setSelectedArt('Art 1 Description')} />
      <BoxWall position={[5, 0, 0]} artwork="/path-to-art2.jpg" onClick={() => setSelectedArt('Art 2 Description')} />
      <BoxWall position={[0, 0, -5]} artwork="/path-to-art3.jpg" onClick={() => setSelectedArt('Art 3 Description')} />
      <OrbitControls />
      {selectedArt && <div className="art-details">{selectedArt}</div>}
    </Canvas>
  );
}

function App() {
  return (
    <div className="App">
      <Room />
      {/* More UI components can go here */}
    </div>
  );
}

export default App;
