// App.js
import React, { useRef, useEffect, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';
import * as THREE from 'three';

function App() {
  const orbitControls = useRef();
  const canvasRef = useRef();
  const [moveSpeed, setMoveSpeed] = useState(0.1);
  const [cameraRotation, setCameraRotation] = useState([0, 0, 0]);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown); // Attach keydown event listener to window
    return () => {
      window.removeEventListener('keydown', handleKeyDown); // Remove keydown event listener when component unmounts
    };
  }, []); // Run effect only once after initial render

  const handleKeyDown = (event) => {
    switch (event.key) {
      case 'ArrowUp':
        setCameraRotation([cameraRotation[0] - 0.01, cameraRotation[1], cameraRotation[2]]);
        break;
      case 'ArrowDown':
        setCameraRotation([cameraRotation[0] + 0.01, cameraRotation[1], cameraRotation[2]]);
        break;
      case 'ArrowLeft':
        setCameraRotation([cameraRotation[0], cameraRotation[1] - 0.01, cameraRotation[2]]);
        break;
      case 'ArrowRight':
        setCameraRotation([cameraRotation[0], cameraRotation[1] + 0.01, cameraRotation[2]]);
        break;
      case 'w':
        orbitControls.current?.dolly(0.1); // Move camera forwards
        break;
      case 's':
        orbitControls.current?.dolly(-0.1); // Move camera backwards
        break;
      default:
        break;
    }
  };

  return (
    <div
      className="App"
      style={{ height: '100vh', width: '100vw' }}
      ref={canvasRef} // Add ref to the canvas
    >
      <Canvas camera={{ position: [0, 1, 2], rotation: cameraRotation, fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls ref={orbitControls} enablePan={false} />
      </Canvas>
    </div>
  );
}

export default App;
