import React, { useState, useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

function App() {
  const [cameraPosition, setCameraPosition] = useState([0, 1, 2]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowUp') {
        setCameraPosition((prev) => {
          const newPos = [prev[0], prev[1], prev[2] - 0.1]; // Move forward
          if (newPos[2] > -9) { // Check if the position is within the room's bounds
            return newPos;
          }
          return prev;
        });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: cameraPosition, fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls enableZoom={false} enablePan={true} enableRotate={true} />
      </Canvas>
    </div>
  );
}

export default App;
