import React, { useRef, useEffect } from 'react';
import { Canvas, extend, useThree, useFrame } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { AmbientLight, PointLight, Vector3, Euler } from 'three';
import Room from './Room';

extend({ OrbitControls });

function CameraControls() {
  const {
    camera,
    gl: { domElement },
  } = useThree();
  const controls = useRef();
  const velocity = useRef(new Vector3());
  const direction = useRef(new Euler(0, 0, 0, 'YXZ'));
  const moveForward = useRef(false);
  const moveBackward = useRef(false);

  useFrame(() => {
    camera.getWorldDirection(direction.current);
    direction.current.x = 0; // Neutralize unwanted x-axis movement
    direction.current.normalize(); // Normalize to ensure constant movement speed

    const speed = 5; // Adjust speed here
    velocity.current.set(0, 0, 0);

    if (moveForward.current) {
      velocity.current.z -= speed;
    }
    if (moveBackward.current) {
      velocity.current.z += speed;
    }

    velocity.current.applyEuler(direction.current);
    camera.position.add(velocity.current);
    controls.current.update();
  });

  useEffect(() => {
    const onKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = true;
          break;
        case 'ArrowDown':
          moveBackward.current = true;
          break;
        case 'ArrowLeft':
          camera.rotation.y += 0.1;
          break;
        case 'ArrowRight':
          camera.rotation.y -= 0.1;
          break;
      }
    };

    const onKeyUp = (event) => {
      switch (event.key) {
        case 'ArrowUp':
        case 'ArrowDown':
          moveForward.current = false;
          moveBackward.current = false;
          break;
      }
    };

    document.addEventListener('keydown', onKeyDown);
    document.addEventListener('keyup', onKeyUp);

    return () => {
      document.removeEventListener('keydown', onKeyDown);
      document.removeEventListener('keyup', onKeyUp);
    };
  }, [camera]);

  return <orbitControls ref={controls} args={[camera, domElement]} enableZoom={true} enablePan={false} />;
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1.5} color="#ffffff" />
        <Room />
        <CameraControls />
      </Canvas>
    </div>
  );
}

export default App;
