import React from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Plane } from '@react-three/drei';
import * as THREE from 'three';

function BoxWall({ position, rotation, scale, color }) {
  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <planeGeometry args={[scale[0], scale[1]]} />
      <meshStandardMaterial color={color} side={THREE.DoubleSide} />
    </mesh>
  );
}

function Room({ showCeiling = false }) {
  const walls = [
    { position: [-2.5, 0, 0], rotation: [0, Math.PI / 2, 0], scale: [5, 5, 1], color: 'white' },
    { position: [2.5, 0, 0], rotation: [0, -Math.PI / 2, 0], scale: [5, 5, 1], color: 'white' },
    { position: [0, 0, -2.5], rotation: [0, 0, 0], scale: [5, 5, 1], color: 'white' },
    { position: [0, 0, 2.5], rotation: [0, Math.PI, 0], scale: [5, 5, 1], color: 'white' },
    showCeiling && { position: [0, 2.5, 0], rotation: [-Math.PI / 2, 0, 0], scale: [5, 5, 1], color: 'white' },
    { position: [0, -2.5, 0], rotation: [Math.PI / 2, 0, 0], scale: [5, 5, 1], color: 'lightgrey' }
  ].filter(Boolean);

  return (
    <>
      {walls.map((wall, index) => (
        <BoxWall key={index} {...wall} />
      ))}
      <OrbitControls enableZoom={true} enablePan={true} enableRotate={true} />
    </>
  );
}


function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 5], fov: 75 }}>
        <ambientLight intensity={0.5} />
        <pointLight position={[0, 2, 0]} intensity={1.5} />
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
