import { useRef, useEffect } from 'react';
import { useThree, useFrame } from '@react-three/fiber';
import * as THREE from 'three';

function CameraControls() {
  const { camera } = useThree();
  const moveForward = useRef(false);
  const moveBackward = useRef(false);
  const turnLeft = useRef(false);
  const turnRight = useRef(false);

  useFrame(() => {
    if (moveForward.current) {
      camera.translateZ(-0.1); // Moves the camera forward
    }
    if (moveBackward.current) {
      camera.translateZ(0.1); // Moves the camera backward
    }
    if (turnLeft.current) {
      camera.rotation.y += 0.02; // Rotates the camera to the left
    }
    if (turnRight.current) {
      camera.rotation.y -= 0.02; // Rotates the camera to the right
    }

    // Clamping the position to ensure the camera stays within the room
    camera.position.clamp(new THREE.Vector3(-9.5, camera.position.y, -9.5), new THREE.Vector3(9.5, camera.position.y, 9.5));
  });

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = true;
          break;
        case 'ArrowDown':
          moveBackward.current = true;
          break;
        case 'ArrowLeft':
          turnLeft.current = true;
          break;
        case 'ArrowRight':
          turnRight.current = true;
          break;
        default:
          break;
      }
    };

    const handleKeyUp = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = false;
          break;
        case 'ArrowDown':
          moveBackward.current = false;
          break;
        case 'ArrowLeft':
          turnLeft.current = false;
          break;
        case 'ArrowRight':
          turnRight.current = false;
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return null; // Since this component does not render anything
}

export default CameraControls;
