import React, { useRef, useEffect } from 'react';
import { Canvas, useThree, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

function Controls() {
  const { camera, gl: { domElement } } = useThree();
  const moveForward = useRef(false);
  const moveBackward = useRef(false);
  const velocity = useRef(new THREE.Vector3()); // Movement speed and direction
  const direction = useRef(new THREE.Vector3()); // Forward direction of the camera

  useFrame((state, delta) => {
    camera.getWorldDirection(direction.current);
    const speed = 5 * delta; // Speed can be adjusted for more realistic movement
    if (moveForward.current) {
      velocity.current.set(direction.current.x, 0, direction.current.z).normalize().multiplyScalar(speed);
      camera.position.add(velocity.current);
    }
    if (moveBackward.current) {
      velocity.current.set(direction.current.x, 0, direction.current.z).normalize().multiplyScalar(-speed);
      camera.position.add(velocity.current);
    }

    // Clamping the camera position to stay within room boundaries
    camera.position.x = THREE.MathUtils.clamp(camera.position.x, -10, 10);
    camera.position.z = THREE.MathUtils.clamp(camera.position.z, -10, 10);
  });

  useEffect(() => {
    const onKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = true;
          break;
        case 'ArrowDown':
          moveBackward.current = true;
          break;
        case 'ArrowLeft':
          camera.rotation.y += 0.05; // Adjust this value to change rotation speed
          break;
        case 'ArrowRight':
          camera.rotation.y -= 0.05; // Adjust this value to change rotation speed
          break;
      }
    };

    const onKeyUp = (event) => {
      if (event.key === 'ArrowUp' || event.key === 'ArrowDown') {
        moveForward.current = false;
        moveBackward.current = false;
      }
    };

    document.addEventListener('keydown', onKeyDown);
    document.addEventListener('keyup', onKeyUp);

    return () => {
      document.removeEventListener('keydown', onKeyDown);
      document.removeEventListener('keyup', onKeyUp);
    };
  }, [camera]);

  return null;
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls enableZoom={false} enablePan={false} enableRotate={false} />
        <Controls />
      </Canvas>
    </div>
  );
}

export default App;
