import React, { useEffect } from 'react';
import { Canvas, useThree, useFrame } from '@react-three/fiber';
import { OrbitControls, PerspectiveCamera } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

function Controls() {
  const { camera, gl: { domElement } } = useThree();

  useFrame(() => {
    // This will run every frame; you can implement some animation logic here if needed.
  });

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          // Move forward
          camera.position.z -= 0.5;
          break;
        case 'ArrowDown':
          // Move backward
          camera.position.z += 0.5;
          break;
        case 'ArrowLeft':
          // Rotate left
          camera.rotation.y += 0.05;
          break;
        case 'ArrowRight':
          // Rotate right
          camera.rotation.y -= 0.05;
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [camera]);

  return (
    <OrbitControls args={[camera, domElement]} enableZoom={false} enablePan={false} enableRotate={true} />
  );
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas>
        <PerspectiveCamera makeDefault position={[0, 1, 2]} fov={75} />
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <Controls />
      </Canvas>
    </div>
  );
}

export default App;
