import React, { useRef, useEffect } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

function App() {
  const { camera } = useThree();
  const moveForward = useRef(false);
  const moveBackward = useRef(false);
  const rotateLeft = useRef(false);
  const rotateRight = useRef(false);

  useEffect(() => {
    const onKeyDown = (event) => {
      switch (event.code) {
        case "ArrowUp":
          moveForward.current = true;
          break;
        case "ArrowDown":
          moveBackward.current = true;
          break;
        case "ArrowLeft":
          rotateLeft.current = true;
          break;
        case "ArrowRight":
          rotateRight.current = true;
          break;
        default:
          break;
      }
    };

    const onKeyUp = (event) => {
      switch (event.code) {
        case "ArrowUp":
          moveForward.current = false;
          break;
        case "ArrowDown":
          moveBackward.current = false;
          break;
        case "ArrowLeft":
          rotateLeft.current = false;
          break;
        case "ArrowRight":
          rotateRight.current = false;
          break;
        default:
          break;
      }
    };

    document.addEventListener("keydown", onKeyDown);
    document.addEventListener("keyup", onKeyUp);

    return () => {
      document.removeEventListener("keydown", onKeyDown);
      document.removeEventListener("keyup", onKeyUp);
    };
  }, []);

  useFrame(() => {
    if (moveForward.current) {
      camera.position.z -= 0.1;
    }
    if (moveBackward.current) {
      camera.position.z += 0.1;
    }
    if (rotateLeft.current) {
      camera.rotation.y += 0.05;
    }
    if (rotateRight.current) {
      camera.rotation.y -= 0.05;
    }
  });

  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls enableZoom={false} enablePan={false} enableRotate={false} />
      </Canvas>
    </div>
  );
}

export default App;
