import React, { useRef, useEffect } from 'react';
import { useThree } from '@react-three/fiber';
import { OrbitControls, Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  const controls = useRef();
  const { camera } = useThree();

  // Define the wall material
  const wallMaterial = new THREE.MeshStandardMaterial({
    color: '#f0f0f0', // Light grey
    roughness: 0.5,   // Adjust for matte appearance
    metalness: 0.1    // Very low reflectivity
  });

  // Lock the camera within a certain distance
  useEffect(() => {
    const currentControls = controls.current;
    currentControls.maxDistance = 5; // Set max distance for zoom
    currentControls.minDistance = 2; // Set min distance for zoom
    return () => {
      currentControls.dispose();
    };
  }, [controls]);

  return (
    <>
      <OrbitControls ref={controls} enableZoom={false} />
      <Plane args={[10, 10]} position={[-5, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[10, 10]} position={[5, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[10, 10]} position={[0, 0, -5]} material={wallMaterial} />
      <Plane args={[10, 10]} position={[0, 0, 5]} rotation={[0, Math.PI, 0]} material={wallMaterial} />
      <Plane args={[10, 10]} position={[0, 5, 0]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} /> {/* Ceiling */}
      <Plane args={[10, 10]} position={[0, -5, 0]} rotation={[Math.PI / 2, 0, 0]} material={wallMaterial} /> {/* Floor */}
    </>
  );
}

export default Room;
