// src/App.js
import React from 'react';
import { Canvas } from '@react-three/fiber';
import Room from './Room'; // Import the Room component

function App() {
  return (
    <div style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 2, 10], fov: 60 }}>
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
