import React from 'react';
import { Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  // Uniform light grey material
  const uniformMaterial = new THREE.MeshStandardMaterial({
    color: '#f8f8f8', // A very light grey, nearly white
    roughness: 0.5,   // Some roughness to diffuse reflection
    metalness: 0.1    // Minimal metalness to keep surfaces matte
  });

  return (
    <>
      {/* Walls */}
      <Plane args={[20, 10]} position={[-10, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={uniformMaterial} />
      <Plane args={[20, 10]} position={[10, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={uniformMaterial} />
      <Plane args={[20, 10]} position={[0, 0, 10]} rotation={[0, Math.PI, 0]} material={uniformMaterial} />
      <Plane args={[20, 10]} position={[0, 0, -10]} material={uniformMaterial} />

      {/* Floor */}
      <Plane args={[20, 10]} position={[0, -10, 0]} rotation={[Math.PI / 2, 0, 0]} material={uniformMaterial} />

      {/* Ceiling */}
      <Plane args={[20, 10]} position={[0, 10, 0]} rotation={[-Math.PI / 2, 0, 0]} material={uniformMaterial} />

      {/* Optional: Skylight (if you decide to try it again) */}
      {/* <Plane args={[10, 10]} position={[0, 10.1, 0]} rotation={[-Math.PI / 2, 0, 0]} material={{color: 'skyblue', transparent: true, opacity: 0.5}} /> */}
    </>
  );
}

export default Room;
