import React, { Suspense, useRef, useState, useEffect } from 'react';
import { Canvas, useLoader, useThree } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';
import * as THREE from 'three';

function BoxWall({ position, rotation, scale, artwork }) {
  const texture = useLoader(THREE.TextureLoader, artwork);

  // Adjust texture settings to ensure proper brightness and color accuracy
  texture.minFilter = THREE.LinearFilter; // Improves texture loading at smaller scales

  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <planeGeometry args={[scale[0], scale[1]]} />
      <meshStandardMaterial map={texture} side={THREE.DoubleSide} color="white" />
    </mesh>
  );
}

function Room() {
  const [selectedArt, setSelectedArt] = useState(null);
  const { camera, gl: { domElement } } = useThree();
  const controls = useRef();

  useEffect(() => {
    const checkBounds = () => {
      camera.position.clamp(new THREE.Vector3(-2, 1, -2), new THREE.Vector3(2, 3, 2));
    };

    const currentControls = controls.current;
    currentControls.addEventListener('change', checkBounds);
    return () => currentControls.removeEventListener('change', checkBounds);
  }, [camera, controls]);

  return (
    <>
      <OrbitControls ref={controls} args={[camera, domElement]} enableZoom={false} enablePan={false} enableRotate={true} />
      <Suspense fallback={null}>
        {/* BoxWall components as previously defined */}
      </Suspense>
      {selectedArt && (
        <Html position={[0, 0, 0]} center>
          <div className="art-details" style={{ background: 'white', padding: '10px' }}>
            {selectedArt}
          </div>
        </Html>
      )}
    </>
  );
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
