// App.js
import React, { useRef, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

function App() {
  const [enableOrbitControls, setEnableOrbitControls] = useState(true);
  const controls = useRef();

  const handleKeyDown = (event) => {
    if (event.key.startsWith('Arrow')) {
      setEnableOrbitControls(false);
      const moveDistance = 0.5;
      const rotateAngle = Math.PI / 36;

      switch (event.key) {
        case 'ArrowUp':
          controls.current.moveForward(moveDistance);
          break;
        case 'ArrowDown':
          controls.current.moveForward(-moveDistance);
          break;
        case 'ArrowLeft':
          controls.current.rotateLeft(rotateAngle);
          break;
        case 'ArrowRight':
          controls.current.rotateRight(rotateAngle);
          break;
        default:
          break;
      }
    }
  };

  const handleKeyUp = () => {
    setEnableOrbitControls(true);
  };

  return (
    <div
      className="App"
      style={{ height: '100vh', width: '100vw' }}
      onKeyDown={handleKeyDown}
      onKeyUp={handleKeyUp}
      tabIndex={0} // Ensure the div can receive focus
    >
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls
          ref={controls}
          enableZoom={false}
          enablePan={false}
          enableRotate={enableOrbitControls}
        />
      </Canvas>
    </div>
  );
}

export default App;
