// src/Room.js
import React from 'react';
import { useLoader } from '@react-three/fiber';
import { Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  const texture = useLoader(THREE.TextureLoader, 'assets/images/wall.jpg');
  texture.wrapS = THREE.RepeatWrapping;
  texture.wrapT = THREE.RepeatWrapping;
  texture.repeat.set(4, 4); // Adjust depending on your texture and preference

  return (
    <>
      {/* Floor and Ceiling */}
      <Plane args={[10, 10]} rotation={[-Math.PI / 2, 0, 0]} position={[0, -2.5, 0]} material={{ map: texture }} />
      <Plane args={[10, 10]} rotation={[Math.PI / 2, 0, 0]} position={[0, 2.5, 0]} material={{ map: texture }} />
      {/* Walls */}
      <Plane args={[10, 5]} position={[-5, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={{ map: texture }} />
      <Plane args={[10, 5]} position={[5, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={{ map: texture }} />
      <Plane args={[10, 5]} position={[0, 0, 5]} rotation={[0, Math.PI, 0]} material={{ map: texture }} />
      <Plane args={[10, 5]} position={[0, 0, -5]} material={{ map: texture }} />
    </>
  );
}

export default Room;
