import { useRef, useEffect } from 'react';
import { useThree, useFrame } from '@react-three/fiber';

function CameraControls() {
  const { camera } = useThree();
  const moveForward = useRef(false);
  const moveBackward = useRef(false);

  // Assume the front and back walls are located at z = 10 and z = -10 respectively
  const zFrontWall = 10;
  const zBackWall = -10;

  useFrame(() => {
    if (moveForward.current && camera.position.z < zFrontWall) {
      camera.position.z += 0.1; // Move the camera forward
    }
    if (moveBackward.current && camera.position.z > zBackWall) {
      camera.position.z -= 0.1; // Move the camera backward
    }
  });

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowUp') {
        moveForward.current = true;
      }
      if (event.key === 'ArrowDown') {
        moveBackward.current = true;
      }
    };

    const handleKeyUp = (event) => {
      if (event.key === 'ArrowUp') {
        moveForward.current = false;
      }
      if (event.key === 'ArrowDown') {
        moveBackward.current = false;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [camera]);

  return null; // Since this component does not render anything
}

export default CameraControls;
