import React, { useRef, useState } from 'react';
import { useLoader } from '@react-three/fiber';
import { Html } from '@react-three/drei';
import * as THREE from 'three';

function Wall({ position, rotation, scale, textureUrl }) {
  const texture = useLoader(THREE.TextureLoader, textureUrl);
  texture.wrapS = THREE.RepeatWrapping;
  texture.wrapT = THREE.RepeatWrapping;
  texture.repeat.set(1, 1); // Adjust the repeat for texture according to your needs

  return (
    <mesh position={position} rotation={rotation}>
      <boxGeometry args={scale} />
      <meshStandardMaterial map={texture} side={THREE.DoubleSide} />
    </mesh>
  );
}

function Room() {
  const [selectedArt, setSelectedArt] = useState(null);

  const onClickHandler = (artDescription) => {
    setSelectedArt(artDescription);
  };

  return (
    <group>
      {/* Walls and floor, ceiling setup */}
      <Wall position={[0, 2.5, -5]} rotation={[0, 0, 0]} scale={[10, 5, 0.1]} textureUrl="/textures/wall1.jpg" />
      <Wall position={[0, 2.5, 5]} rotation={[0, Math.PI, 0]} scale={[10, 5, 0.1]} textureUrl="/textures/wall2.jpg" />
      <Wall position={[-5, 2.5, 0]} rotation={[0, Math.PI / 2, 0]} scale={[10, 5, 0.1]} textureUrl="/textures/wall3.jpg" />
      <Wall position={[5, 2.5, 0]} rotation={[0, -Math.PI / 2, 0]} scale={[10, 5, 0.1]} textureUrl="/textures/wall4.jpg" />
      <Wall position={[0, 5, 0]} rotation={[-Math.PI / 2, 0, 0]} scale={[10, 10, 0.1]} textureUrl="/textures/ceiling.jpg" />
      <Wall position={[0, 0, 0]} rotation={[Math.PI / 2, 0, 0]} scale={[10, 10, 0.1]} textureUrl="/textures/floor.jpg" />

      {selectedArt && (
        <Html position={[0, 1, 0]} center>
          <div className="art-details" style={{ background: 'white', padding: '10px' }}>
            {selectedArt}
          </div>
        </Html>
      )}
    </group>
  );
}

export default Room;
