import React, { Suspense, useRef, useState, useEffect } from 'react';
import { Canvas, useLoader, extend, useThree } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';
import * as THREE from 'three';

function BoxWall({ position, rotation, scale, artwork }) {
  const texture = useLoader(THREE.TextureLoader, artwork);
  texture.encoding = THREE.sRGBEncoding; // Ensures the texture uses the sRGB color space

  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <planeGeometry args={[scale[0], scale[1]]} />
      <meshStandardMaterial map={texture} side={THREE.DoubleSide} color="white" roughness={1} metalness={0} />
    </mesh>
  );
}

function Room() {
  const { camera, gl } = useThree();
  const controls = useRef();

  useEffect(() => {
    camera.position.set(0, 1, 2); // Center the camera in the room
    controls.current.update();
  }, [camera]);

  return (
    <>
      <ambientLight intensity={0.8} color="#ffffff" />
      <pointLight position={[10, 10, 10]} intensity={1} color="#ffffff" />
      <OrbitControls ref={controls} target={[0, 1, 0]} enableZoom={false} />
      <Suspense fallback={null}>
        {/* Walls, ceiling, and floor */}
      </Suspense>
    </>
  );
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas>
        <Room />
      </Canvas>
    </div>
  );
}

export default App;
