import React, { useEffect } from 'react';
import { Canvas, useThree } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three'; // Correct import for THREE
import Room from './Room';

function CameraControls() {
  const { camera } = useThree();

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          // Move forward
          camera.translateZ(-0.1);
          break;
        case 'ArrowDown':
          // Move backward
          camera.translateZ(0.1);
          break;
        case 'ArrowLeft':
          // Rotate left
          camera.rotation.y += 0.05;
          break;
        case 'ArrowRight':
          // Rotate right
          camera.rotation.y -= 0.05;
          break;
      }
      // Keep the camera within the room
      camera.position.clamp(new THREE.Vector3(-9.5, 1, -9.5), new THREE.Vector3(9.5, 1, 9.5));
    };
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [camera]);

  return null;
}

function App() {
  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls enableZoom={false} enablePan={false} enableRotate={false} />
        <CameraControls />
      </Canvas>
    </div>
  );
}

export default App;
