import React from 'react';
import { Plane } from '@react-three/drei';
import * as THREE from 'three';

function Room() {
  const wallMaterial = new THREE.MeshStandardMaterial({
    color: '#f0f0f0',
    roughness: 0.5,
    metalness: 0.1
  });

  return (
    <>
      {/* Walls */}
      <Plane args={[20, 10]} position={[-10, 0, 0]} rotation={[0, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[10, 0, 0]} rotation={[0, -Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[0, 0, 10]} rotation={[0, Math.PI, 0]} material={wallMaterial} />
      <Plane args={[20, 10]} position={[0, 0, -10]} material={wallMaterial} />

      {/* Floor */}
      <Plane args={[20, 10]} position={[0, -10, 0]} rotation={[Math.PI / 2, 0, 0]} material={wallMaterial} />

      {/* Ceiling with a skylight */}
      {/* Main ceiling */}
      <Plane args={[18, 10]} position={[0, 10, -5]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} />
      <Plane args={[18, 10]} position={[0, 10, 5]} rotation={[-Math.PI / 2, 0, 0]} material={wallMaterial} />
      <Plane args={[2, 18]} position={[-9, 10, 0]} rotation={[-Math.PI / 2, Math.PI / 2, 0]} material={wallMaterial} />
      <Plane args={[2, 18]} position={[9, 10, 0]} rotation={[-Math.PI / 2, Math.PI / 2, 0]} material={wallMaterial} />

      {/* Skylight */}
      {/* This could be a transparent material or even open to the sky */}
      {/* Adjust the size or position based on your aesthetic need */}
    </>
  );
}

export default Room;
