// App.js
import React, { useRef, useEffect, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

function App() {
  const [enableOrbitControls, setEnableOrbitControls] = useState(true);
  const controls = useRef();

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (controls.current && event.key.startsWith('Arrow')) {
        setEnableOrbitControls(false);
        const moveDistance = 0.5;

        switch (event.key) {
          case 'ArrowUp':
            if (controls.current.position && controls.current.position.z) {
              controls.current.position.z -= moveDistance;
            }
            break;
          case 'ArrowDown':
            if (controls.current.position && controls.current.position.z) {
              controls.current.position.z += moveDistance;
            }
            break;
          case 'ArrowLeft':
            if (controls.current.position && controls.current.position.x) {
              controls.current.position.x -= moveDistance;
            }
            break;
          case 'ArrowRight':
            if (controls.current.position && controls.current.position.x) {
              controls.current.position.x += moveDistance;
            }
            break;
          default:
            break;
        }
      }
    };

    const handleKeyUp = () => {
      setEnableOrbitControls(true);
    };

    // Attach event listeners when the component mounts
    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);

    // Remove event listeners when the component unmounts
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, []); // Empty dependency array to ensure the effect runs only once

  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" />
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" />
        <Room />
        <OrbitControls
          ref={controls}
          enableZoom={false}
          enablePan={false}
          enableRotate={enableOrbitControls}
        />
      </Canvas>
    </div>
  );
}

export default App;
