import React, { useEffect, useRef } from 'react';
import { Canvas, useThree } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { AmbientLight, PointLight } from 'three';
import Room from './Room';

function App() {
  const { camera } = useThree();
  const moveForward = (distance) => {
    camera.position.z -= distance;
  };
  const moveBackward = (distance) => {
    camera.position.z += distance;
  };
  const moveLeft = (angle) => {
    camera.rotation.y += angle;
  };
  const moveRight = (angle) => {
    camera.rotation.y -= angle;
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward(0.1);
          break;
        case 'ArrowDown':
          moveBackward(0.1);
          break;
        case 'ArrowLeft':
          moveLeft(0.05);
          break;
        case 'ArrowRight':
          moveRight(0.05);
          break;
        default:
          break;
      }
    };
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [camera]);

  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }}>
        <ambientLight intensity={0.8} color="#ffffff" /> {/* Increased ambient light */}
        <pointLight position={[0, 10, 0]} intensity={1300} color="#ffffff" /> {/* Strong central light */}
        <Room />
        <OrbitControls enableZoom={false} enablePan={true} enableRotate={true} />
      </Canvas>
    </div>
  );
}

export default App;
