import React, { useRef, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { AmbientLight, PointLight, Vector3 } from 'three';
import Room from './Room';

function App() {
  const cameraRef = useRef();
  const moveForward = useRef(false);
  const moveBackward = useRef(false);
  const turnLeft = useRef(false);
  const turnRight = useRef(false);

  useFrame(() => {
    if (!cameraRef.current) return;

    const delta = 0.1; // Speed of the movement
    if (moveForward.current) {
      cameraRef.current.translateZ(-delta);
    }
    if (moveBackward.current) {
      cameraRef.current.translateZ(delta);
    }
    if (turnLeft.current) {
      cameraRef.current.rotation.y += 0.02;
    }
    if (turnRight.current) {
      cameraRef.current.rotation.y -= 0.02;
    }
  });

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = true;
          break;
        case 'ArrowDown':
          moveBackward.current = true;
          break;
        case 'ArrowLeft':
          turnLeft.current = true;
          break;
        case 'ArrowRight':
          turnRight.current = true;
          break;
      }
    };

    const handleKeyUp = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          moveForward.current = false;
          break;
        case 'ArrowDown':
          moveBackward.current = false;
          break;
        case 'ArrowLeft':
          turnLeft.current = false;
          break;
        case 'ArrowRight':
          turnRight.current = false;
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return (
    <div className="App" style={{ height: '100vh', width: '100vw' }}>
      <Canvas camera={{ position: [0, 1, 2], fov: 75 }} ref={cameraRef}>
        <ambientLight intensity={0.8} />
        <pointLight position={[0, 10, 0]} intensity={1.3} />
        <Room />
        <OrbitControls enableZoom={false} enablePan={false} enableRotate={false} />
      </Canvas>
    </div>
  );
}

export default App;
